-- =====================================================

-- Question 1: Find employees whose salary is above the average salary for their department
-- Reasoning: This uses a subquery to calculate the average salary per department,
-- then joins it with the employees table to compare individual salaries against their department average.

SELECT 
    e.name, 
    e.department_id, 
    e.role, 
    e.salary,
    ROUND(dept_avg.avg_salary, 2) as department_avg_salary,
    ROUND(((e.salary - dept_avg.avg_salary) / dept_avg.avg_salary) * 100, 2) as percentage_above_avg
FROM employees e
JOIN (
    SELECT 
        department_id,
        AVG(salary) as avg_salary
    FROM employees
    GROUP BY department_id
) dept_avg ON e.department_id = dept_avg.department_id
WHERE e.salary > dept_avg.avg_salary
ORDER BY e.department_id, e.salary DESC;

