-- Question 2: Retrieve customer details for all customers who have placed orders over ₦1000
-- Reasoning: Uses a subquery with IN clause to find customers who have at least one order above ₦1000,
-- then retrieves their complete details including contact info and preferences.
SELECT 
    c.customer_id,
    c.name,
    c.email,
    c.phone,
    JSON_UNQUOTE(JSON_EXTRACT(c.address, '$.city')) as city,
    JSON_UNQUOTE(JSON_EXTRACT(c.address, '$.zip')) as zip_code,
    JSON_UNQUOTE(JSON_EXTRACT(c.preferences, '$.marketing_opt_in')) as marketing_opt_in,
    JSON_EXTRACT(c.preferences, '$.categories') as preferred_categories,
    COUNT(o.order_id) as total_orders,
    SUM(o.total_amount) as total_spent,
    MAX(o.total_amount) as highest_order_amount
FROM customers c
JOIN orders o ON c.customer_id = o.customer_id
WHERE c.customer_id IN (
    SELECT DISTINCT customer_id
    FROM orders
    WHERE total_amount > 1000
)
GROUP BY c.customer_id, c.name, c.email, c.phone, c.address, c.preferences
ORDER BY total_spent DESC;

