-- Question 3 & 4: Find orders where total amount > customer's average AND 
-- customer's total spending > global average
-- Reasoning: Uses CTEs to calculate customer statistics and global averages,
-- then filters orders based on both conditions. This identifies high-value orders
-- from high-spending customers.

WITH customer_stats AS (
    -- Calculate each customer's average order amount and total spending
    SELECT 
        customer_id,
        AVG(total_amount) as avg_order_amount,
        SUM(total_amount) as total_spending,
        COUNT(*) as total_orders,
        MIN(total_amount) as min_order,
        MAX(total_amount) as max_order
    FROM orders
    GROUP BY customer_id
),
global_averages AS (
    -- Calculate global average customer spending
    SELECT 
        AVG(total_spending) as global_avg_spending,
        AVG(avg_order_amount) as global_avg_order_amount,
        AVG(total_orders) as global_avg_order_count
    FROM customer_stats
)
SELECT 
    o.order_id,
    c.name as customer_name,
    c.email,
    JSON_UNQUOTE(JSON_EXTRACT(c.address, '$.city')) as customer_city,
    o.customer_id,
    o.order_date,
    o.total_amount,
    o.status,
    JSON_UNQUOTE(JSON_EXTRACT(o.metadata, '$.source')) as order_source,
    JSON_UNQUOTE(JSON_EXTRACT(o.metadata, '$.coupon')) as coupon_used,
    ROUND(cs.avg_order_amount, 2) as customer_avg_order,
    ROUND(cs.total_spending, 2) as customer_total_spending,
    ROUND(ga.global_avg_spending, 2) as global_avg_spending,
    cs.total_orders as customer_order_count,
    ROUND((o.total_amount / cs.avg_order_amount - 1) * 100, 2) as pct_above_personal_avg,
    ROUND((cs.total_spending / ga.global_avg_spending - 1) * 100, 2) as pct_above_global_avg
FROM orders o
JOIN customers c ON o.customer_id = c.customer_id
JOIN customer_stats cs ON o.customer_id = cs.customer_id
CROSS JOIN global_averages ga
WHERE o.total_amount > cs.avg_order_amount
    AND cs.total_spending > ga.global_avg_spending
    AND o.status IN ('Delivered', 'Shipped', 'Processing')  -- Exclude cancelled/pending orders
ORDER BY o.total_amount DESC, cs.total_spending DESC;